// Helper function to get all articles
function getAllArticles() {
    return articlesDatabase;
}

// Helper function to get article by ID
function getArticleById(id) {
    return articlesDatabase.find(article => article.id === id);
}

// Helper function to get articles by category
function getArticlesByCategory(category) {
    return articlesDatabase.filter(article => 
        article.category.toLowerCase() === category.toLowerCase()
    );
}

// Helper function to get featured articles
function getFeaturedArticles() {
    return articlesDatabase.filter(article => article.featured);
}

// Helper function to search articles
function searchArticles(query) {
    const lowerQuery = query.toLowerCase();
    return articlesDatabase.filter(article => 
        article.title.toLowerCase().includes(lowerQuery) ||
        article.subtitle.toLowerCase().includes(lowerQuery) ||
        article.category.toLowerCase().includes(lowerQuery) ||
        article.tags.some(tag => tag.toLowerCase().includes(lowerQuery))
    );
}

// Helper function to get related articles
function getRelatedArticles(articleId, limit = 3) {
    const article = getArticleById(articleId);
    if (!article) return [];
    
    return articlesDatabase
        .filter(a => a.id !== articleId && a.category === article.category)
        .slice(0, limit);
}
